function App({Icon = 'home' }) {
    const [content, setContent ] = useState('Test Content');

    function handleClick(selectedButton) {
      setContent(selectedButton)
    }

    const SetIcon = {Icon} ? <span>{Icon}</span> : null;
    console.log(SetIcon)
  return (
    <div className="App">
      {/* <SetIcon /> */}
